import React, { useContext } from 'react';
import { Grid, Typography, Paper, makeStyles, Button } from '@material-ui/core';
import { Mic, MicOff, Videocam, VideocamOff } from '@material-ui/icons';

import { SocketContext } from '../Context';
import '../video_styles.css';

const useStyles = makeStyles((theme) => ({
  video: {
    width: '400px',
    [theme.breakpoints.down('xs')]: {
      width: '300px',
    },
  },
  gridContainer: {
    justifyContent: 'center',
    [theme.breakpoints.down('xs')]: {
      width: '80%',
    },
  },
  buttonGridContainer: {
    position: 'relative',
    width: '100%',
    display: 'flex',
    [theme.breakpoints.down('xs')]: {
      flexDirection: 'column',
    },
  },
  margin: {
    marginTop: 10,
  },
  padding: {
    padding: 10,
  },
  paper: {
    padding: '10px',
    border: '2px',
    margin: '10px',
  },
}));

const VideoPlayer = () => {
  const { name, callAccepted, myVideo, userVideo, callEnded, stream, call, toggleMic, toggleVideo } = useContext(SocketContext);
  const classes = useStyles();

  return (
    <Grid container className={classes.gridContainer}>
      {stream && (
        <Paper className={classes.paper}>
          <Grid item xs={12} md={6}>
            <Typography variant="h5" gutterBottom>{name || 'Name'}</Typography>
            <video playsInline muted ref={myVideo} autoPlay className={classes.video} />
            <Grid container className={classes.buttonGridContainer}>
              <Grid item xs={12} md={6} className={classes.padding}>
                <Button variant="contained" color="primary" fullWidth startIcon={<Mic fontSize="large" />} onClick={() => toggleMic()} className={classes.margin}>
                </Button>
              </Grid>
              <Grid item xs={12} md={6} className={classes.padding}>
                <Button variant="contained" color="primary" fullWidth startIcon={<Videocam fontSize="large" />} onClick={() => toggleVideo()} className={classes.margin}>
                </Button>
              </Grid>
            </Grid>
          </Grid>
        </Paper>
      )}
      {callAccepted && !callEnded && (
        <Paper className={classes.paper}>
          <Grid item xs={12} md={6}>
            <Typography variant="h5" gutterBottom>{call.name || 'Name'}</Typography>
            <video playsInline ref={userVideo} autoPlay className={classes.video} />
            <Grid container className={classes.buttonGridContainer}>
              <Grid item xs={12} md={6} className={classes.padding}>
              {toggleMic ? (
                <Button variant="contained" color="primary" startIcon={<Mic fontSize="large" />} fullWidth oonClick={() => toggleMic()} className={classes.margin}>

                </Button>
              ) : (
                <Button variant="contained" color="primary" startIcon={<MicOff fontSize="large" />} fullWidth onClick={() => toggleMic()} className={classes.margin}>

                </Button>
              )}
              {toggleVideo ? (
                <Button variant="contained" color="primary" startIcon={<Videocam fontSize="large" />} fullWidth oonClick={() => toggleMic()} className={classes.margin}>
                  Hang Up
                </Button>
              ) : (
                <Button variant="contained" color="primary" startIcon={<VideocamOff fontSize="large" />} fullWidth onClick={() => toggleVideo()} className={classes.margin}>
                  Call
                </Button>
              )}
                <Button variant="contained" color="primary" startIcon={<Mic fontSize="large" />} fullWidth onClick={() => toggleVideo()} className={classes.margin}>
                </Button>
              </Grid>
              <Grid item xs={12} md={6} className={classes.padding}>
                <Button variant="contained" color="primary" startIcon={<Videocam fontSize="large" />} fullWidth onClick={() => toggleVideo()} className={classes.margin}>
                </Button>
              </Grid>
            </Grid>
          </Grid>
        </Paper>
      )}
    </Grid>
  );
};

export default VideoPlayer;
